define({"topics" : [{"title":"Record and Playback","shortdesc":"\n                  <p class=\"shortdesc\">The   metaphor for record and playback is the test tool as a tape recorder.   </p>\n               ","id":"ABT_record_playback","href":"TA_Help\/Topics\/ABT_record_playback.html","menu": {"hasChildren":false,},"tocID":"ABT_record_playback-d46e30657","topics":[]},{"title":"Scripting","shortdesc":"\n                  <p class=\"shortdesc\">Scripting, or programming, commonly refers to a process in which test cases are first developed by testers, usually in verbal\n                     form, and then automated, usually by a specialized automation engineer. \n                  </p>\n               ","id":"ABT_Scripting","href":"TA_Help\/Topics\/ABT_Scripting.html","menu": {"hasChildren":false,},"tocID":"ABT_Scripting-d46e30670","topics":[]},{"title":"Keywords, Action Based Testingâ„¢","shortdesc":"\n                  <p class=\"shortdesc\">In a keyword approach, the tester creates the test cases as a series of keywords with   arguments. </p>\n               ","id":"ABT_Keywords","href":"TA_Help\/Topics\/ABT_Keywords.html","menu": {"hasChildren":false,},"tocID":"ABT_Keywords-d46e30681","topics":[]}]});