define({"topics" : [{"title":"Creating a user-defined field","shortdesc":"\n               <p class=\"shortdesc\">There may be times when you&apos;ll want to augment the set of fields that <span class=\"ph\">TestArchitect</span> provides for a given project item type. You can         do that by creating new fields and adding them to the item type \n                  definition.\n               </p>\n            ","id":"user_defined_fields_create","href":"TA_Administration\/Topics\/User_defined_fields_create.html","menu": {"hasChildren":false,},"tocID":"user_defined_fields_create-d46e31504","topics":[]},{"title":"Editing a user-defined field","shortdesc":"\n               <p class=\"shortdesc\">You can edit the <span class=\"ph uicontrol\">Name</span>, <span class=\"ph uicontrol\">Short Name</span>, or             <span class=\"ph uicontrol\"> Default Value</span> properties of all user-defined fields.\n               </p>\n            ","id":"user_defined_fields_edit","href":"TA_Administration\/Topics\/User_defined_fields_edit.html","menu": {"hasChildren":false,},"tocID":"user_defined_fields_edit-d46e31518","topics":[]},{"title":"Deleting a user-defined field","shortdesc":"\n               <p class=\"shortdesc\">If you no longer need a user-defined field, you can delete it.</p>\n            ","id":"user_defined_fields_delete","href":"TA_Administration\/Topics\/User_defined_fields_delete.html","menu": {"hasChildren":false,},"tocID":"user_defined_fields_delete-d46e31538","topics":[]}]});