define({"topics" : [{"title":"Configuring user-defined help","shortdesc":"\n                        <p class=\"shortdesc\">To make use of Help-system     extensibility for custom actions, you must modify the mapping file     <span class=\"ph filepath\">user_defined_help.ini</span>, along with the corresponding commands.\n                        </p>\n                     ","id":"Additional_feaures_help_configuration","href":"TA_Help\/Topics\/Additional_features_Help_extensibility_configuration.html","menu": {"hasChildren":false,},"tocID":"Additional_feaures_help_configuration-d46e19503","topics":[]},{"title":"Displaying action descriptions","shortdesc":"\n                        <p class=\"shortdesc\">The primary value of the Help System Extensibility feature is in allowing you to     provide reference pages documenting your\n                           custom actions.  Context-sensitive help functionality     allows you to offer testers and automation engineers easy access\n                           to the actions&apos; definitions and     application notes.\n                        </p>\n                     ","id":"Additional_feaures_help_sensitive_displaying","href":"TA_Help\/Topics\/Additional_features_Help_extensibility_sensitive_help.html","menu": {"hasChildren":false,},"tocID":"Additional_feaures_help_sensitive_displaying-d46e19517","topics":[]}]});