define({"topics" : [{"title":"Configuring Google Chrome","shortdesc":"\n                           <p class=\"shortdesc\">To ensure that automated web application testing in <span class=\"ph\">Google Chrome</span> runs properly with <span class=\"ph\">TestArchitect</span>, you first need to set some necessary command         line switches.\n                           </p>\n                        ","id":"task_xvs_qg4_q4_copy","href":"TA_Automation\/Topics\/aut_setting_switches_GC_COPY.html","menu": {"hasChildren":false,},"tocID":"task_xvs_qg4_q4_copy-d46e20420","topics":[]},{"title":"Setting 100% zoom","shortdesc":"\n                           <p class=\"shortdesc\"><span class=\"ph\">TestArchitect</span> requires that the browser zoom be set to         100% to correctly identify HTML controls and their positions on web pages.\n                           </p>\n                        ","id":"task_qrh_5dv_z4_copy","href":"TA_Automation\/Topics\/aut_app_testing_setting_zoom_GC_COPY.html","menu": {"hasChildren":false,},"tocID":"task_qrh_5dv_z4_copy-d46e20437","topics":[]}]});