define({"topics" : [{"title":"Enabling <span class=\"ph\">TestArchitect Automation Agent</span>","shortdesc":"\n                              <p class=\"shortdesc\">Enabling the <span class=\"ph\">TestArchitect Automation Agent</span>         in <span class=\"ph\">Internet Explorer</span> is required for testing web-based applications         with <span class=\"ph\">TestArchitect</span> in that browser.\n                              </p>\n                           ","id":"task_lvr_vvn_q4","href":"TA_Automation\/Topics\/aut_enabling_automation_agent_IE.html","menu": {"hasChildren":false,},"tocID":"task_lvr_vvn_q4-d46e44053","topics":[]},{"title":"Advanced settings","shortdesc":"\n                              <p class=\"shortdesc\">Some settings in <span class=\"ph\">Internet Explorer</span> must be set correctly to         ensure that <span class=\"ph\">TestArchitect</span> can properly conduct automated         tests within the browser.\n                              </p>\n                           ","id":"task_w3c_hgc_fq","href":"TA_Automation\/Topics\/aut_advanced_settings_IE.html","menu": {"hasChildren":false,},"tocID":"task_w3c_hgc_fq-d46e44075","topics":[]},{"title":"Setting 100% zoom","shortdesc":"\n                              <p class=\"shortdesc\"><span class=\"ph\">TestArchitect</span> requires that the browser zoom be set to         100% to correctly identify HTML controls and their positions on web pages.\n                              </p>\n                           ","id":"task_v4t_tbv_z4","href":"TA_Automation\/Topics\/aut_app_testing_setting_zoom_IE.html","menu": {"hasChildren":false,},"tocID":"task_v4t_tbv_z4-d46e44092","topics":[]}]});