define({"topics" : [{"title":"Introduction","shortdesc":"\n               <p class=\"shortdesc\">Lab Manager is a web-based application that can be accessed through any browser from    virtually anywhere within an organization.\n                  \n               </p>\n            ","id":"Lab_manager_Introduction","href":"TA_Help\/Topics\/Lab_manager_intro.html","menu": {"hasChildren":false,},"tocID":"Lab_manager_Introduction-d46e28545","topics":[]},{"title":"Installation and configuration","shortdesc":"\n               <p class=\"shortdesc\">Lab Manager is installed automatically when <span class=\"ph\">TestArchitect</span> is installed.\n               </p>\n            ","id":"Lab_manager_Installation_configuration","href":"TA_Help\/Topics\/Lab_manager_install_configure.html","menu": {"hasChildren":false,},"tocID":"Lab_manager_Installation_configuration-d46e28556","topics":[]},{"title":"Authentication and access rights","shortdesc":"\n               <p class=\"shortdesc\">Lab Manager uses the same login credentials and has the same access rights as those of   a <span class=\"ph\">TestArchitect</span>repository.\n               </p>\n            ","id":"Lab_manager_Authentication_access_rights","href":"TA_Help\/Topics\/Lab_manager_authentication.html","menu": {"hasChildren":false,},"tocID":"Lab_manager_Authentication_access_rights-d46e28570","topics":[]},{"title":"Starting Lab Manager","shortdesc":"\n               <p class=\"shortdesc\">Lab Manager may be started from <span class=\"ph\">TestArchitect Client</span>, a Dashboard   instance, or any browser on machines with a connection to the web server running the Lab Manager   application.\n               </p>\n            ","id":"Lab_manager_starting","href":"TA_Help\/Topics\/Lab_manager_starting.html","menu": {"hasChildren":false,},"tocID":"Lab_manager_starting-d46e28584","next":"Lab_manager_starting-d46e28584",},{"title":"Managing automation lab","shortdesc":"\n               <p class=\"shortdesc\">A Lab Manager session displays and monitors all those <span class=\"ph\">TestArchitect</span> controllers that are registered to the session. \n               </p>\n            ","id":"Lab_manager_Managing_automation_lab","href":"TA_Help\/Topics\/Lab_manager_managing_auto_lab.html","menu": {"hasChildren":false,},"tocID":"Lab_manager_Managing_automation_lab-d46e28634","next":"Lab_manager_Managing_automation_lab-d46e28634",},{"title":"Troubleshooting controller registration","shortdesc":"\n               <p class=\"shortdesc\">This topic lists typical issues you may experience when registering controllers to a     Lab Manager session, and offers possible\n                  solutions.\n               </p>\n            ","id":"Lab_manage_troubleshooting_controller_registration","href":"TA_Help\/Topics\/Lab_manager_troubleshoot_controller_registration.html","menu": {"hasChildren":false,},"tocID":"Lab_manage_troubleshooting_controller_registration-d46e28725","topics":[]},{"title":"Troubleshooting device registration","shortdesc":"\n               <p class=\"shortdesc\">This topic lists typical issues you may experience when registering devices to a Lab     Manager session, and offers possible\n                  solutions.\n               </p>\n            ","id":"reference_mqz_t2g_2l","href":"TA_Help\/Topics\/Lab_manager_troubleshoot_device_registration.html","menu": {"hasChildren":false,},"tocID":"reference_mqz_t2g_2l-d46e28736","topics":[]}]});