define({"topics" : [{"title":"Lab Manager toolbar","shortdesc":"\n                  <p class=\"shortdesc\">The Lab Manager toolbar consists of several buttons for managing the     controllers\/devices, test runs, and the display.\n                     \n                  </p>\n               ","id":"Lab_manager_toolbar","href":"TA_Help\/Topics\/Lab_manager_toolbar.html","menu": {"hasChildren":false,},"tocID":"Lab_manager_toolbar-d46e28648","topics":[]},{"title":"Registering controllers","shortdesc":"\n                  <p class=\"shortdesc\">How to register a controller from Lab Manager.</p>\n               ","id":"Lab_manager_Registering_controller_from_Lab-Manager","href":"TA_Help\/Topics\/Lab_manager_registering.html","menu": {"hasChildren":false,},"tocID":"Lab_manager_Registering_controller_from_Lab-Manager-d46e28659","topics":[]},{"title":"Self-registering a controller","shortdesc":"\n                  <p class=\"shortdesc\">A controller can register itself with a Lab Manager session.</p>\n               ","id":"task_f1b_nw3_rl","href":"TA_Help\/Topics\/Lab_manager_self_registering.html","menu": {"hasChildren":false,},"tocID":"task_f1b_nw3_rl-d46e28670","topics":[]},{"title":"Special note regarding porting virtual machine images ","shortdesc":"\n                  <p class=\"shortdesc\">Lab Manager allows for the seamless transport of a cloned controller.</p>\n               ","id":"Lab_manager_notes","href":"TA_Help\/Topics\/Lab_manager_notes.html","menu": {"hasChildren":false,},"tocID":"Lab_manager_notes-d46e28681","topics":[]},{"title":"Registering devices","shortdesc":"\n                  <p class=\"shortdesc\">Registering a device from within a Lab Manager session.</p>\n               ","id":"task_gvw_xwx_dl","href":"TA_Help\/Topics\/Lab_manager_registering_device.html","menu": {"hasChildren":false,},"tocID":"task_gvw_xwx_dl-d46e28692","topics":[]},{"title":"Controller status","shortdesc":"\n                  <p class=\"shortdesc\">You can view, within a Lab Manager session, the current status of each registered     controller, including the results of\n                     test runs (both after-the-fact and in real time) and other     information. You can also abort tests on individual controllers,\n                     and remove controllers from the     session.\n                  </p>\n               ","id":"Lab_manager_controller_status","href":"TA_Help\/Topics\/Lab_manager_controller.html","menu": {"hasChildren":false,},"tocID":"Lab_manager_controller_status-d46e28703","topics":[]},{"title":"Device status","shortdesc":"\n                  <p class=\"shortdesc\">In a Lab Manager session, you can view the current status (in real time or     after-the-fact) of each registered device,\n                     and mark devices for removal or test termination. \n                  </p>\n               ","id":"Lab_manager_device_status","href":"TA_Help\/Topics\/Lab_manager_device.html","menu": {"hasChildren":false,},"tocID":"Lab_manager_device_status-d46e28714","topics":[]}]});