define({"topics" : [{"title":"Single license server environment","shortdesc":"\n                     <p class=\"shortdesc\">A single license server handles and grants licensing requests from    <span class=\"ph\">TestArchitect</span> Clients or controllers on your network.\n                     </p>\n                  ","id":"License_server_single_license","href":"TA_Administration\/Topics\/LS_TA_single_license_2.html","menu": {"hasChildren":false,},"tocID":"License_server_single_license-d46e32680","topics":[]},{"title":"Host-based licenses","shortdesc":"\n                     <p class=\"shortdesc\">Host-based licensing is appropriate when it is unacceptable for a host to be denied a     license from the license server.\n                        Host-based licensing allows a host to receive a license even     when the pool of available floating licenses has been exhausted.\n                        \n                     </p>\n                  ","id":"License_server_Host_based_licenses","href":"TA_Administration\/Topics\/LS_TA_host_based_license_2.html","menu": {"hasChildren":false,},"tocID":"License_server_Host_based_licenses-d46e32694","topics":[]},{"title":"Multiple license servers","shortdesc":"\n                     <p class=\"shortdesc\">Several license servers can be used to administer licenses to clients. For example, one     license server may handle only\n                        agent licensing, while another dedicated license server may     administer full client\/controller licensing.\n                     </p>\n                  ","id":"License_server_multiple_license","href":"TA_Administration\/Topics\/LS_TA_multiple_license_2.html","menu": {"hasChildren":false,},"tocID":"License_server_multiple_license-d46e32705","topics":[]},{"title":"Redundant license servers","shortdesc":"\n                     <p class=\"shortdesc\">A redundant license server operates in a network in tandem with another server   designated as the main, or primary, license\n                        server. Redundant license servers assume the role of   administering licenses when the primary server is offline or unable\n                        to administer   licenses.\n                     </p>\n                  ","id":"License_server_Redundant_license","href":"TA_Administration\/Topics\/LS_TA_Redundant_license_2.html","menu": {"hasChildren":false,},"tocID":"License_server_Redundant_license-d46e32716","topics":[]}]});