define({"topics" : [{"title":"Acquiring a temporary Universal license","shortdesc":"\n                     <p class=\"shortdesc\">Within a working session, you can obtain a temporary <span class=\"ph\"><span class=\"keyword option\">Universal</span></span> license to enable all features available in <span class=\"ph\">TestArchitect</span>.\n                     </p>\n                  ","id":"Acquiring_temporary_Enterprise_license","href":"TA_Administration\/Topics\/LS_TA_acquiring_temporary_enterprise_license_3.html","menu": {"hasChildren":false,},"tocID":"Acquiring_temporary_Enterprise_license-d46e33788","topics":[]},{"title":"Converting a floating license into a host-based license","shortdesc":"\n                     <p class=\"shortdesc\">Any floating license can be reconfigured to act as a host-based license,       thereby restricting that license&apos;s availability\n                        to one specific machine.\n                     </p>\n                  ","id":"Converting_floating_to_host_based","href":"TA_Administration\/Topics\/LS_TA_converting_floating_to_host_based_3.html","menu": {"hasChildren":false,},"tocID":"Converting_floating_to_host_based-d46e33806","topics":[]},{"title":"Converting a host-based license into a floating license","shortdesc":"\n                     <p class=\"shortdesc\">Any host-based license can be reconfigured to act as a floating license,       thereby freeing that license from a specific\n                        machine. \n                     </p>\n                  ","id":"Converting_host_based_to_floating_license","href":"TA_Administration\/Topics\/LS_TA_converting_host_based_to_floating_3.html","menu": {"hasChildren":false,},"tocID":"Converting_host_based_to_floating_license-d46e33817","topics":[]}]});