define({"topics" : [{"title":"Assigning a node-locked license","shortdesc":"\n                     <p class=\"shortdesc\">Assigning a node-locked license binds that license to a machine for a given period of     time.</p>\n                  ","id":"Assigning_Node-Locked_license","href":"TA_Administration\/Topics\/LS_TA_assigning_node-locked_license.html","menu": {"hasChildren":false,},"tocID":"Assigning_Node-Locked_license-d46e32181","topics":[]},{"title":"Transferring a node-locked machine license","shortdesc":"\n                     <p class=\"shortdesc\">Within     certain limits, you are free to transfer a node-locked license from one machine to another, as     you might wish\n                        to do when upgrading to a new test machine.\n                     </p>\n                  ","id":"Changing_node-locked_machine.dita","href":"TA_Administration\/Topics\/LS_TA_changing_node-locked_machine.html","menu": {"hasChildren":false,},"tocID":"Changing_node-locked_machine.dita-d46e32192","topics":[]}]});