define({"topics" : [{"title":"Create a new project","shortdesc":"\n                  <p class=\"shortdesc\">You create a project to organize and store project information in <span class=\"ph\">TestArchitect</span>.\n                  </p>\n               ","id":"Projects_and_tests_create_project","href":"TA_Help\/Topics\/Projects_and_project_items_create_project.html","menu": {"hasChildren":false,},"tocID":"Projects_and_tests_create_project-d46e20517","topics":[]},{"title":"Delete a project","shortdesc":"\n                  <p class=\"shortdesc\">Deleting a project removes the entire project, including actions, tests, data, results,   etc., from the repository.</p>\n               ","id":"Projects_and_tests_delete_project","href":"TA_Help\/Topics\/Projects_and_project_items_delete_project.html","menu": {"hasChildren":false,},"tocID":"Projects_and_tests_delete_project-d46e20531","topics":[]},{"title":"Rename a project","shortdesc":"\n                  <p class=\"shortdesc\">Renaming a project only changes the project&apos;s name and not its content.</p>\n               ","id":"Rename_project","href":"TA_Help\/Topics\/Projects_and_project_items_rename_project.html","menu": {"hasChildren":false,},"tocID":"Rename_project-d46e20542","topics":[]},{"title":"Display latest test result","shortdesc":"\n                  <p class=\"shortdesc\">You can quickly display the latest test result for a project from the project&apos;s context   menu.</p>\n               ","id":"Projects_and_tests_get_latest_result","href":"TA_Help\/Topics\/Projects_and_project_items_display_latest_test_result.html","menu": {"hasChildren":false,},"tocID":"Projects_and_tests_get_latest_result-d46e20553","topics":[]},{"title":"Project subscription","shortdesc":"\n                  <p class=\"shortdesc\"><span class=\"ph\">Project subscription aids in large-scale, complex test         development. Tests distributed over multiple projects are allowed\n                        to share actions,         interface definitions, data sets, and picture checks. (Note that, in the case of picture       \n                        checks, only <a class=\"xref\" href=\"TA_Glossary\/Topics\/glossarySharedPictureCheck.html\"><em class=\"ph i\">shared</em> picture checks</a>         can be subscribed to.)</span></p>\n               ","id":"Project_subscription","href":"TA_Help\/Topics\/Project_subscription.html","menu": {"hasChildren":false,},"tocID":"Project_subscription-d46e20572","next":"Project_subscription-d46e20572",},{"title":"Execute test","shortdesc":"\n                  <p class=\"shortdesc\">After creating a project, adding necessary test modules, and defining relevant actions     and interfaces for those test modules,\n                     you are ready to execute the projectâ€™s test     modules.\n                  </p>\n               ","id":"Projects_execute_test","href":"TA_Help\/Topics\/Projects_execute_test.html","menu": {"hasChildren":false,},"tocID":"Projects_execute_test-d46e20626","topics":[]},{"title":"Assign user","shortdesc":"\n                  <p class=\"shortdesc\">You can restrict access to a project by granting permissions to selected users.</p>\n               ","id":"Projects_assign_user","href":"TA_Help\/Topics\/Projects_assign_user.html","menu": {"hasChildren":false,},"tocID":"Projects_assign_user-d46e20637","topics":[]},{"title":"Search","shortdesc":"\n                  <p class=\"shortdesc\"><span class=\"ph\">TestArchitect</span> searches the entire project and returns   all tokens matching the searched items.\n                  </p>\n               ","id":"Projects_search","href":"TA_Help\/Topics\/Projects_search.html","menu": {"hasChildren":false,},"tocID":"Projects_search-d46e20650","topics":[]},{"title":"Reporting","shortdesc":"\n                  <p class=\"shortdesc\">Reports can be created for the entire project or a subtree of the project.</p>\n               ","id":"Projects_reporting","href":"TA_Help\/Topics\/Projects_reporting.html","menu": {"hasChildren":false,},"tocID":"Projects_reporting-d46e20665","topics":[]}]});