define({"topics" : [{"title":"Projects in TestArchitect","shortdesc":"\n               <p class=\"shortdesc\">A    <span class=\"ph\">TestArchitect</span>   <dfn class=\"term\">project</dfn> is a container for all assets required for your testing efforts relating to a   specific project.      A project is included\n                  in a repository, and may coexist with other projects in that   repository.\n               </p>\n            ","id":"Projects","href":"TA_Help\/Topics\/Projects_def.html","menu": {"hasChildren":false,},"tocID":"Projects-d46e20500","next":"Projects-d46e20500",},{"title":"Project items","shortdesc":"\n               <p class=\"shortdesc\"><span class=\"ph\">TestArchitect</span> has the facility to manage, control, view   and edit project items.\n               </p>\n            ","id":"Project_items_def","href":"TA_Help\/Topics\/Project_items_def.html","menu": {"hasChildren":false,},"tocID":"Project_items_def-d46e20678","next":"Project_items_def-d46e20678",}]});