define({"topics" : [{"title":"Creating reports","shortdesc":"\n                  <p class=\"shortdesc\">Reports can be produced for entire projects or for specified project items.</p>\n               ","id":"Report_producing","href":"TA_Help\/Topics\/Report_producing.html","menu": {"hasChildren":false,},"tocID":"Report_producing-d46e27759","topics":[]},{"title":"Customizing reports","shortdesc":"\n                  <p class=\"shortdesc\"><span class=\"ph\">TestArchitect</span>offers a wealth of features to allow you to     create reports on your projects and tests, and customize those reports to\n                     meet your unique     needs. \n                  </p>\n               ","id":"Report_customizing","href":"TA_Help\/Topics\/Report_customizing.html","menu": {"hasChildren":false,},"tocID":"Report_customizing-d46e27770","topics":[]},{"title":"Saving report profiles","shortdesc":"\n                  <p class=\"shortdesc\">After setting up a customized report, you may want to save the settings you have created     so that future reports with those\n                     same settings can be readily generated. TestArchitect makes     this possible with <span class=\"keyword\">report profiles</span>.\n                  </p>\n               ","id":"Save_profile","href":"TA_Help\/Topics\/Report_profiles.html","menu": {"hasChildren":false,},"tocID":"Save_profile-d46e27783","topics":[]},{"title":"Deleting custom report profiles","shortdesc":"\n                  <p class=\"shortdesc\">Deleting a saved report profile removes the profile from the       <span class=\"ph uicontrol\">Reporting</span> context menu and the <span class=\"ph uicontrol\">Report Profile</span>     list.\n                  </p>\n               ","id":"delete_profile","href":"TA_Help\/Topics\/Delete_profiles.html","menu": {"hasChildren":false,},"tocID":"delete_profile-d46e27797","topics":[]}]});