define({"topics" : [{"title":"Reporting","shortdesc":"\n               <p class=\"shortdesc\">With     <span class=\"ph\">TestArchitect</span>&apos;s powerful reporting features, customized reports    can be generated to show current and historical data based on the various\n                  criteria that you    specify. You can readily show the progress of test development, test run performance and result    trends,\n                  and a host of other aspects of a project. \n               </p>\n            ","id":"Reporting","href":"TA_Help\/Topics\/Reporting_def.html","menu": {"hasChildren":false,},"tocID":"Reporting-d46e27743","next":"Reporting-d46e27743",},{"title":"Dashboard","shortdesc":"\n               <p class=\"shortdesc\">This section describes concepts of <span class=\"ph\">TestArchitect</span>&apos;s <span class=\"ph\">Dashboard</span> and shows you how to use the <span class=\"ph\">Dashboard</span>   to support your testing tasks, track progress and display results.\n               </p>\n            ","id":"Dashboard","href":"TA_Help\/Topics\/Dashboard.html","menu": {"hasChildren":false,},"tocID":"Dashboard-d46e27822","next":"Dashboard-d46e27822",}]});