define({"topics" : [{"title":"Creating an uninstallation response file","shortdesc":"\n                  <p class=\"shortdesc\">To remove previously installed <span class=\"ph\">TestArchitect</span> in silent         mode by using <span class=\"ph filepath\">InstallShield</span>, create an uninstallation response file to         store the needed uninstallation settings. \n                  </p>\n               ","id":"task_ccv_p4w_5w","href":"TA_InstallationGuide\/DITA_source\/Topics\/uninst_TA_silent_mode_creating_response_file.html","menu": {"hasChildren":false,},"tocID":"task_ccv_p4w_5w-d46e19256","topics":[]},{"title":"Running silent uninstallation","shortdesc":"\n                  <p class=\"shortdesc\">Deploying <span class=\"ph\">TestArchitect</span> uninstallation file on other         computers and running the uninstallation there using the created response file.\n                  </p>\n               ","id":"task_xhk_r4w_5w","href":"TA_InstallationGuide\/DITA_source\/Topics\/uninst_TA_silent_mode_running_response_file.html","menu": {"hasChildren":false,},"tocID":"task_xhk_r4w_5w-d46e19273","topics":[]},{"title":"Verifying the silent uninstallation success","shortdesc":"\n                  <p class=\"shortdesc\">In silent mode, the uninstallation wizard shows no informative, warning or error     messages. The uninstallation program\n                     creates the log file and writes the uninstallation status     to that file. You can examine that log file to learn whether\n                     <span class=\"ph\">TestArchitect</span> has been removed successfully.\n                  </p>\n               ","id":"concept_yj2_s4w_5w","href":"TA_InstallationGuide\/DITA_source\/Topics\/uninst_TA_silent_mode_verifying_response_file.html","menu": {"hasChildren":false,},"tocID":"concept_yj2_s4w_5w-d46e19287","topics":[]}]});