define({"topics" : [{"title":"Configuring WebDriver server","shortdesc":"\n                              <p class=\"shortdesc\">WebDriver allows you to create automated tests that simulate user interaction. For         security purposes, WebDriver is\n                                 disabled by default. In order to use WebDriver, you will         need to download and install the Microsoft WebDriver.\n                              </p>\n                           ","id":"task_g3p_lcy_sw","href":"TA_Automation\/Topics\/aut_app_testing_webdriver_ME.html","menu": {"hasChildren":false,},"tocID":"task_g3p_lcy_sw-d46e44009","topics":[]},{"title":"Setting 100% zoom","shortdesc":"\n                              <p class=\"shortdesc\"><span class=\"ph\">TestArchitect</span> requires that the browser zoom be set to         100% to correctly identify HTML controls and their positions on web pages.\n                              </p>\n                           ","id":"task_al2_ztx_sw","href":"TA_Automation\/Topics\/aut_app_testing_setting_zoom_ME.html","menu": {"hasChildren":false,},"tocID":"task_al2_ztx_sw-d46e44020","topics":[]}]});