define({"topics" : [{"title":"Preparing a web browser ","shortdesc":"\n                        <p class=\"shortdesc\">To allow <span class=\"ph\">TestArchitect</span> to work properly with a     Silverlight application embedded into a web page, you may need to prepare your web     browser.\n                        </p>\n                     ","id":"concept_btr_n1v_z4","href":"TA_Automation\/Topics\/aut_app_testing_Silverlight_apps_prepraring_browser.html","menu": {"hasChildren":false,},"tocID":"concept_btr_n1v_z4-d46e44780","topics":[]},{"title":"Installing a Certificate Authority","shortdesc":"\n                        <p class=\"shortdesc\">In order to test Silverlight applications running over the HTTPS protocol, installing         a <span class=\"ph\">TestArchitect</span> Certificate Authority (CA) on your test         machine is required.\n                        </p>\n                     ","id":"task_dry_ps4_lp","href":"TA_Automation\/Topics\/aut_app_testing_Silverlight_apps_installing_CA.html","menu": {"hasChildren":false,},"tocID":"task_dry_ps4_lp-d46e44794","topics":[]},{"title":"Configuring a test machine for Silverlight","shortdesc":"\n                        <p class=\"shortdesc\">Preparing your test machine to ensure the testability of your Silverlight         application.</p>\n                     ","id":"task_kdt_whc_1p","href":"TA_Automation\/Topics\/aut_app_testing_Silverlight_apps_preparation.html","menu": {"hasChildren":false,},"tocID":"task_kdt_whc_1p-d46e44808","topics":[]}]});