define({"topics" : [{"title":"Changing user account during installation process","shortdesc":"\n                  <p class=\"shortdesc\">During the installation process, rather than accept the default user account to the         repository server, you may specify\n                     your desired account to perform that.\n                  </p>\n               ","id":"task_jg4_4kv_nv","href":"TA_Administration\/Topics\/adm_changing_OS_account_installation.html","menu": {"hasChildren":false,},"tocID":"task_jg4_4kv_nv-d46e31155","topics":[]},{"title":"Changing user account via the <span class=\"keyword wintitle\">Repository Server Control Panel</span> dialog box\n               ","shortdesc":"\n                  <p class=\"shortdesc\">If, during the installation process, you do not specify a desired user account to         start the repository server, you\n                     are still allowed to change a different user account from         the <span class=\"keyword wintitle\">Repository Server Control Panel</span> dialog box. \n                  </p>\n               ","id":"task_rpy_x3v_nv","href":"TA_Administration\/Topics\/adm_changing_OS_account_RS_dlg.html","menu": {"hasChildren":false,},"tocID":"task_rpy_x3v_nv-d46e31166","topics":[]}]});