define({"topics" : [{"title":"Configuring iOS devices and macOS machines","shortdesc":"\n                           <p class=\"shortdesc\">This section describes essential configurations needed to ensure that automated tests         on Safari iOS run successfully\n                              and stably.\n                           </p>\n                        ","id":"task_u23_zv3_vp","href":"TA_Automation\/Topics\/aut_app_testing_mobile_web_Safari_configurations.html","menu": {"hasChildren":false,},"tocID":"task_u23_zv3_vp-d46e45704","topics":[]},{"title":"Installing TestArchitect Browser Agent","shortdesc":"\n                           <p class=\"shortdesc\">If your tests are to make use of the built-in <span class=\"keyword apiname\">navigate</span> action to         automatically invoke web pages in the Safari browser, then any iOS device subject to such         testing\n                              must by prepped by having TestArchitect Browser Agent re-signed and installed.         (Note, however, that this procedure\n                              may be bypassed if you instead plan to start Safari         manually by tapping the Safari icon on the device.)\n                           </p>\n                        ","id":"task_e5k_23c_vp","href":"TA_Automation\/Topics\/aut_app_testing_mobile_web_Safari_installing_TA_Browser_Agent.html","menu": {"hasChildren":false,},"tocID":"task_e5k_23c_vp-d46e45715","topics":[]}]});