define({"topics" : [{"title":"Field selection and display","shortdesc":"\n                        <p class=\"shortdesc\">You can perform basic functions on a list view, such as resizing the width and height     of the list view, or selecting which\n                           columns to display.\n                        </p>\n                     ","id":"list_view_field_selection","href":"TA_Help\/Topics\/Listview_field_selection.html","menu": {"hasChildren":false,},"tocID":"list_view_field_selection-d46e21324","topics":[]},{"title":"Sorting","shortdesc":"\n                        <p class=\"shortdesc\">Sorting all elements of a list view into ascending or descending order.</p>\n                     ","id":"list_view_sorting","href":"TA_Help\/Topics\/Listview_field_sorting.html","menu": {"hasChildren":false,},"tocID":"list_view_sorting-d46e21335","topics":[]},{"title":"Copy Text","shortdesc":"\n                        <p class=\"shortdesc\">You can copy selected (contiguous or noncontiguous) rows from an item list view and     paste them into a text document or\n                           spreadsheet.\n                        </p>\n                     ","id":"list_view_copy_text","href":"TA_Help\/Topics\/Listview_copy_text.html","menu": {"hasChildren":false,},"tocID":"list_view_copy_text-d46e21346","topics":[]},{"title":"Filters","shortdesc":"\n                        <p class=\"shortdesc\">Use filters to reduce the set of items displayed in an item list view, and to limit   operations to those items.</p>\n                     ","id":"list_view_filtering","href":"TA_Help\/Topics\/Listview_filtering.html","menu": {"hasChildren":false,},"tocID":"list_view_filtering-d46e21357","topics":[]},{"title":"Editing multiple fields","shortdesc":"\n                        <p class=\"shortdesc\">You can modify the field values of multiple items in an item list view by selecting       <span class=\"ph uicontrol\">Edit Fields</span> from the context menu.\n                        </p>\n                     ","id":"list_view_field_editing_multiple_fields","href":"TA_Help\/Topics\/Listview_edit_fields.html","menu": {"hasChildren":false,},"tocID":"list_view_field_editing_multiple_fields-d46e21368","topics":[]}]});