define({"topics" : [{"title":"Suppressing message dialogs","shortdesc":"\n                     <p class=\"shortdesc\">Use the <span class=\"keyword apiname\">set notice level</span> action to specify the types of         problem-related messages that are inhibited from being displayed during the test run.\n                        \n                     </p>\n                  ","id":"error_handling_suppress_messages","href":"TA_Automation\/Topics\/error_handling_suppress_messages.html","menu": {"hasChildren":false,},"tocID":"error_handling_suppress_messages-d46e35631","topics":[]},{"title":"Escaping on error","shortdesc":"\n                     <p class=\"shortdesc\">You can direct <span class=\"ph\">TestArchitect</span> to escape from the         current flow of the test when an error occurs. \n                     </p>\n                  ","id":"error_handling_escapes","href":"TA_Automation\/Topics\/error_handling_escapes.html","menu": {"hasChildren":false,},"tocID":"error_handling_escapes-d46e35645","topics":[]},{"title":"Taking action on problems","shortdesc":"\n                     <p class=\"shortdesc\">You can provide <span class=\"ph\">TestArchitect</span> with specific         instructions as to what to do when a problem occurs. \n                     </p>\n                  ","id":"error_handling_taking_action","href":"TA_Automation\/Topics\/error_handling_taking_action.html","menu": {"hasChildren":false,},"tocID":"error_handling_taking_action-d46e35659","topics":[]},{"title":"Passing arguments to error handlers","shortdesc":"\n                     <p class=\"shortdesc\">Error and failure handlers can be made more flexible by having them accept arguments. </p>\n                  ","id":"error_handling_passing_args","href":"TA_Automation\/Topics\/error_handling_passing_args.html","menu": {"hasChildren":false,},"tocID":"error_handling_passing_args-d46e35673","topics":[]}]});