mage="" bg_repeat="no-repeat" bg_position="0 0" bg_size="cover" bg_attachment="scroll" row_stylespacer2="" class="" row_tab_style_end="" row_tab_parallax_start="" parallax="0" parallax_speed="0.25" row_tab_parallax_end="" row_tab_video_start="" video_id="" video_quality="default" video_loop="1" video_autoplay="1" video_stoponblur="1" row_tab_video_end="" row_tab_cend=""][mb2pb_col class="mb2pb-column col-sm-6 cnoempty" row_tab_list="" col_tab_cstart="" col_tab_style="" padding_tp="0" padding_bt="0" color_scheme="light" custom_class="" col_tab_style_end="" col_tab_bg="" bg_color="" bg_image="" bg_repeat="no-repeat" bg_position="0 0" bg_size="cover" col_tab_bg_end="" col_tab_cend=""][mb2pb_title tag="h4" subtext="" align="center" size="sm" margin="" color="" class="" admin_label=""][/mb2pb_title][/mb2pb_col][mb2pb_col class="mb2pb-column col-sm-6 cnoempty"][mb2pb_title tag="h5" subtext="" align="left" size="sm" margin="20px 0px" color="" class="title-section" admin_label=""]ABT Overview[/mb2pb_title][mb2pb_title tag="h5" subtext="ABT is a modular-design and action-driven test method that provides a systematic approach to increase the success of automated testing. Modular-design addresses the challenges of test planning and test case management through efficient test organization." align="left" size="sm" margin="10px 0px" color="" class="" admin_label=""][/mb2pb_title][mb2pb_title tag="h5" subtext="Action-driven test development eliminates the majority of the programming work required to automate and maintain tests long-term." align="left" size="sm" margin="" color="" class="" admin_label=""][/mb2pb_title][mb2pb_title tag="h4" subtext="<strong>ABT consists of three parts:</strong>" align="left" size="sm" margin="16px 0 0 0" color="" class="" admin_label=""][/mb2pb_title][mb2pb_title tag="h5" subtext="<ul>\r\n<li> Top-down, modular test planning</li>\r\n<li> Test Module development</li>\r\n<li> Action-driven test authoring</li>\r\n</ul>\r\n" align="left" size="sm" margi